by_address_and_destination.map:
function(doc) {
  if ((doc['type'] == 'Identity') && (doc['address'] != null) && (doc['destination'] != null)) {
    emit([doc['address'], doc['destination']], 1);
  }
}

disabled.map:
function(doc) {
  if (doc.type != 'Identity') {
    return;
  }
  if (typeof doc.user_id === "undefined") {
    emit(doc._id, 1);
  }
}

cert_expiry_by_fingerprint.map:
function(doc) {
  if (doc.type != 'Identity') {
    return;
  }
  if (typeof doc.cert_fingerprints === "object") {
    for (fp in doc.cert_fingerprints) {
      if (doc.cert_fingerprints.hasOwnProperty(fp)) {
        emit(fp, doc.cert_fingerprints[fp]);
      }
    }
  }
}

cert_fingerprints_by_expiry.map:
function(doc) {
  if (doc.type != 'Identity') {
    return;
  }
  if (typeof doc.cert_fingerprints === "object") {
    for (fp in doc.cert_fingerprints) {
      if (doc.cert_fingerprints.hasOwnProperty(fp)) {
        emit(doc.cert_fingerprints[fp], fp);
      }
    }
  }
}

pgp_key_by_email.map:
function(doc) {
  if (doc.type != 'Identity') {
    return;
  }
  if (typeof doc.keys === "object") {
    emit(doc.address, doc.keys["pgp"]);
  }
}
