by_is_open_and_created_at.map:
function(doc) {
  if ((doc['type'] == 'Ticket') && (doc['is_open'] != null) && (doc['created_at'] != null)) {
    emit([doc['is_open'], doc['created_at']], 1);
  }
}

by_is_open_and_updated_at.map:
function(doc) {
  if ((doc['type'] == 'Ticket') && (doc['is_open'] != null) && (doc['updated_at'] != null)) {
    emit([doc['is_open'], doc['updated_at']], 1);
  }
}

by_includes_post_by_and_updated_at.map:
function(doc) {
  var arr = {}
  if (doc['type'] == 'Ticket' && doc.comments) {
    doc.comments.forEach(function(comment){
      if (comment.posted_by && !arr[comment.posted_by]) {
        //don't add duplicates
        arr[comment.posted_by] = true;
        emit([comment.posted_by, doc.updated_at], 1);
      }
    });
  }
}

